const switchLang = (val: string) => {
  currentLang.value = val;
  localStorage.currentLang = currentLang.value;
};

onMounted(() => {
  if (localStorage.currentLang) {
    currentLang.value = localStorage.currentLang;
  } else {
    currentLang.value = navigator.language.split("-")[0];
  }
  console.log(currentLang.value);
});
